import styled from "styled-components";

const Container = styled.div`
  position: relative;
  padding: 20% 25% 0 25%;
  border: 2px solid #3E2723;
  border-radius: 4px;
  max-width: $props{};
  height: 100%;

  &::before {
    content: "";
    position: absolute;
    top: -10px;
    left: 6%;
    width: 88%;
    height: 7px;
    border-radius: 50% 50% 0 0;
    background-color: #3E2723;
  }
`;

export default Container;
