import React from "react";
import styled from "styled-components";

const Button = styled.button`
  background: #3E2723;
  color: #FFFFFF;
  padding: 5px 10px;
  border: 2px solid #3E2723;
  cursor: pointer;
  font-size: 14px;

  &:hover {
    background: #FFFFFF;
    color: #3E2723;
  }
`;

export default Button;
